mapper.delete:
DELETE FROM pen_info
WHERE farm_idx IN (
SELECT farm_idx FROM farm_info WHERE farm_name = #{farm_name}
)

DELETE FROM env_criteria_info
WHERE farm_idx IN (
SELECT farm_idx FROM farm_info WHERE farm_name = #{farm_name}
)

mapper.update:
UPDATE farm_info
SET farm_name = #{newFarmName}, farm_loc = #{farmLoc}, farm_livestock_cnt
= #{farmLivestockCnt}
WHERE farm_name = #{oldFarmName}
